window.addEventListener("scroll",function(){var e=document.querySelector(".header");0<window.scrollY?e.classList.add("header--sticky"):e.classList.remove("header--sticky")});const menuItems=document.querySelectorAll(".header__menu-item a"),hamburgerMenu=(menuItems.forEach(n=>{n.addEventListener("click",function(e){e.preventDefault(),document.querySelector(n.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}),document.querySelector(".hamburger-menu")),headerMenuMobile=document.createElement("div");headerMenuMobile.className="header__menu-mobile",headerMenuMobile.innerHTML=document.querySelector(".header__menu").innerHTML,hamburgerMenu.addEventListener("click",function(){document.body.appendChild(headerMenuMobile)}),document.addEventListener("click",function(e){headerMenuMobile.contains(e.target)||e.target===hamburgerMenu||document.body.removeChild(headerMenuMobile)}),window.addEventListener("resize",function(){var e=document.querySelector(".header");window.innerWidth<=768?e.classList.remove("header--sticky"):e.classList.add("header--sticky")});