window.addEventListener("scroll",function(){var e=document.querySelector(".header");0<window.scrollY?e.classList.add("header--sticky"):e.classList.remove("header--sticky")});const menuItems=document.querySelectorAll(".header__menu-item a");menuItems.forEach(t=>{t.addEventListener("click",function(e){e.preventDefault(),document.querySelector(t.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});